// components/ui/yashika-block.tsx
import * as React from "react"

import { cn } from "@/lib/utils"

interface YashikaBlockProps extends React.HTMLAttributes<HTMLDivElement> {
  bgColor?: string
  borderRadius?: string
  size?: "sm" | "md" | "lg"
  position?: "left" | "center" | "right"
}

export function YashikaBlock({
  bgColor = "bg-pink-500",
  borderRadius = "rounded-2xl",
  size = "md",
  position = "center",
  className,
  ...props
}: YashikaBlockProps) {
  const sizeClasses = {
    sm: "w-24 h-24 text-sm",
    md: "w-40 h-40 text-base",
    lg: "w-60 h-60 text-lg",
  }[size]

  const positionClasses = {
    left: "self-start",
    center: "self-center",
    right: "self-end",
  }[position]

  return (
    <div
      className={cn(
        "flex items-center justify-center font-semibold text-white shadow-md transition-all",
        bgColor,
        borderRadius,
        sizeClasses,
        positionClasses,
        className
      )}
      {...props}
    >
      Yashika
    </div>
  )
}
